module.exports.genericLoadModuleFunction = function(moduleName,logger) {
    return function(comp,cb) {
        try {
            var appModule = comp.require("./" +moduleName)
            cb()
        } catch (e) {
            if (e.code && e.code == "MODULE_NOT_FOUND" && e.toString().indexOf(moduleName)>=0){
                return cb()
            }
            logger.error("exception while processing %s from module %s",appModule,module.filename,e)
            return cb(e)
        }
    }
}