
module.exports.setup = function(cb){
    if (!global._extension_method_called) global._extension_method_called = []
    global._extension_method_called.push("inner_setup")
    cb()
}

module.exports.extend = {
    before:function(cb) {
        global._extension_method_called.push("inner_before")
        cb()
    },
    process:function(comp,cb) {
        global._extension_method_called.push("inner_process," + comp.filename)
        cb()
    },
    after:function(cb) {
        global._extension_method_called.push("inner_after")
        cb()
    }
}

module.exports.wrapup = function(cb) {
    global._extension_method_called.push("inner_wrapup")
    cb()
}
